mapper.update:
UPDATE 
SET supervisor_token = #{newSupervisorToken,jdbcType=VARCHAR},
    version = version+1
WHERE `group` = #{group,jdbcType=VARCHAR}
  AND supervisor_token = #{oldSupervisorToken,jdbcType=VARCHAR}
  AND is_deleted = 0

