mapper.update:
UPDATE 
SET remark = #{remark,jdbcType=VARCHAR}, version = version+1
WHERE job_id = #{jobId,jdbcType=BIGINT}

mapper.select:
SELECT EXISTS (
  SELECT 1
  FROM 
  WHERE `group` = #{group,jdbcType=VARCHAR}
    AND job_name = #{jobName,jdbcType=VARCHAR}
    AND is_deleted = 0
)

mapper.update:
UPDATE 
SET job_state = #{toState,jdbcType=TINYINT},
    version = version+1
WHERE job_id = #{jobId,jdbcType=BIGINT}
  AND job_state = #{fromState,jdbcType=TINYINT}
  AND is_deleted = 0

mapper.select:
SELECT CAST(job_id AS CHAR) id, CONCAT(`group`, ' | ', job_name) text FROM WHERE `group` IN (#{g,jdbcType=VARCHAR}) AND is_deleted = 0
AND ( job_name LIKE #{jobName} OR job_id = #{jobId} )
AND job_name LIKE #{jobName}
AND job_id = #{jobId}
LIMIT 20

mapper.sql:
WHERE is_deleted = 0

    AND `group` IN (#{g,jdbcType=VARCHAR})


    AND job_name LIKE CONCAT('%', #{jobName}, '%')


    AND job_type = #{jobType}


    AND job_state = #{jobState}
