mapper.update:
UPDATE 

  run_state = #{toState,jdbcType=TINYINT},
  instance_id = #{toInstanceId,jdbcType=BIGINT},

WHERE wnstance_id = #{wnstanceId,jdbcType=BIGINT}

    AND cur_node = #{curNode,jdbcType=VARCHAR}

  AND run_state IN (#{state,jdbcType=TINYINT})

    AND instance_id = #{fromInstanceId,jdbcType=BIGINT}

mapper.delete:
DELETE FROM 
WHERE wnstance_id = #{wnstanceId,jdbcType=BIGINT}
  AND (SELECT cnt FROM (SELECT COUNT(*) cnt FROM  WHERE wnstance_id = #{wnstanceId,jdbcType=BIGINT} AND run_state < 40) t)=0
